import React, { useEffect, useState } from 'react'
import {useLocation} from 'react-router-dom';
import './style/Image.css';

function Image() {
    const location = useLocation();
    const [image, setImage] = useState(null)
    console.log("mon image",image?.name)
    const path = window.location.pathname;
    const ImageSlug = path.split("/")[2];
    const token = localStorage.getItem("token")
    useEffect(async () => {
await fetch(`http://localhost:3000/image/${ImageSlug}`, {
        method: "GET",
        headers: {
          Authorization: `Bearer ${token}`,
        },
      }).then((res) => {
        const jason = JSON.stringify(res.json())
        console.log(jason)
      }).catch((e) => console.error(e));
    }, [])
    
     
    return ( <>
    <div className="image-container">
        <img className="image" src={"http://localhost:3000/" + location.state.name}/>
    </div>
    
    </> );
}

export default Image;
